def.body:
matchingOption = false
if ARGV.empty?
  matchingOption = true
  @options[0 + @debugLevel].start
else
  @options.each_with_index do |argument, index|
    if (ARGV[0] == ISM::Default::Option::Debug::ShortText) || (ARGV[0] == ISM::Default::Option::Debug::LongText)
      @debugLevel = 1
    end
    if ((@debugLevel == 0) || ((@debugLevel == 1) && ARGV.size < 2)) || ((@debugLevel == 1) && (ARGV.size == 1))
      if (ARGV[0] == argument.shortText) || (ARGV[0] == argument.longText)
        matchingOption = true
        @options[index].start
        break
      end
    end
    if (@debugLevel == 1) && ARGV.size > 1
      if (ARGV[0 + @debugLevel] == argument.shortText) || (ARGV[0 + @debugLevel] == argument.longText)
        matchingOption = true
        @options[index].start
        break
      end
    end
  end
end
if !matchingOption
  showErrorUnknowArgument
end

if File.exists?(ISM::Default::CommandLineSettings::SettingsFilePath)
  @settings.loadSettingsFile
else
  @settings.writeSettingsFile
end
if File.exists?(ISM::Default::CommandLineSystemSettings::SystemSettingsFilePath)
  @systemSettings.loadSystemSettingsFile
else
  @systemSettings.writeSystemSettingsFile
end
if File.exists?(ISM::Default::CommandLinePortsSettings::PortsSettingsFilePath)
  @portsSettings.loadPortsSettingsFile
else
  @portsSettings.writePortsSettingsFile
end

information = Settings.from_json(File.read(ISM::Default::CommandLineSettings::SettingsFilePath))
@rootPath = information.rootPath
@systemName = information.systemName
@targetName = information.targetName
@architecture = information.architecture
@target = information.target
@makeOptions = information.makeOptions
@buildOptions = information.buildOptions

if ARGV.size == (1 + Ism.debugLevel)
  showHelp
else
  matchingOption = false
  @options.each_with_index do |argument, index|
    if (ARGV[1 + Ism.debugLevel] == argument.shortText) || (ARGV[1 + Ism.debugLevel] == argument.longText)
      matchingOption = true
      @options[index].start
      break
    end
  end
  if (((!matchingOption) && ARGV.size > (2 + Ism.debugLevel)) && (ARGV[2 + Ism.debugLevel] == ISM::Default::Option::SoftwareEnableOption::ShortText)) || (((!matchingOption) && ARGV.size > (2 + Ism.debugLevel)) && (ARGV[2 + Ism.debugLevel] == ISM::Default::Option::SoftwareEnableOption::LongText))
    matchingOption = true
    @options[1].start
  end
  if (((!matchingOption) && ARGV.size > (2 + Ism.debugLevel)) && (ARGV[2 + Ism.debugLevel] == ISM::Default::Option::SoftwareDisableOption::ShortText)) || (((!matchingOption) && ARGV.size > (2 + Ism.debugLevel)) && (ARGV[2 + Ism.debugLevel] == ISM::Default::Option::SoftwareDisableOption::LongText))
    matchingOption = true
    @options[0].start
  end
  if !matchingOption
    puts("#{ISM::Default::CommandLine::ErrorUnknowArgument.colorize(:yellow)}" + "#{ARGV[1 + Ism.debugLevel].colorize(:white)}")
    puts(("#{ISM::Default::CommandLine::ErrorUnknowArgumentHelp1.colorize(:white)}" + "#{ISM::Default::CommandLine::ErrorUnknowArgumentHelp2.colorize(:green)}") + "#{ISM::Default::CommandLine::ErrorUnknowArgumentHelp3.colorize(:white)}")
  end
end

information = Information.from_json(File.read(loadInformationFilePath))
@port = information.port
@name = information.name
@version = information.version
@architectures = information.architectures
@description = information.description
@website = information.website
@downloadLinks = information.downloadLinks
@md5sums = information.md5sums
@patchesLinks = information.patchesLinks
@installedFiles = information.installedFiles
information.dependencies.each do |data|
  dependency = ISM::SoftwareDependency.new
  dependency.name = data.name
  dependency.version = data.version
  data.options.each do |entry|
    option = ISM::SoftwareOption.new
    option.name = entry.name
    option.description = entry.description
    option.active = entry.active
    dependency.options.push(option)
  end
  @dependencies.push(dependency)
end
information.options.each do |data|
  option = ISM::SoftwareOption.new
  option.name = data.name
  option.description = data.description
  option.active = data.active
  @options.push(option)
end
